async function onLogin(props){
    //API Params
    var start = "2020-09-10";
    var end = "2020-09-16";
    var id = props.history.location.state;
    var q = "login"

    //Building API Call
    var url = "https://d7rc7vsgld.execute-api.us-west-2.amazonaws.com/dev/q?id=" + id;
    if(q != ""){
      url += "&q="+q
    }
    if(start != ""){
      url += "&start="+start
    }
    if(end != ""){
      url += "&end="+end
    }

    //Making Call
    const Response = await new Promise((resolve, reject) => {
  	var req = fetch(url, {
  		method: 'GET', // *GET, POST, PUT, DELETE, etc.
  		mode: 'cors', // no-cors, *cors, same-origin
  		cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
  		credentials: 'same-origin', // include, *same-origin, omit
  		headers: {
  		  'Content-Type': 'text/plain',
  		  // 'Content-Type': 'application/x-www-form-urlencoded',
  		},
  		redirect: 'follow', // manual, *follow, error
  		referrerPolicy: 'no-referrer' // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
  	});
  	req.then((result) => {
  	    var res = result.json()
  	    res.then((tmp) => {
  	        resolve({
      		    statusCode: 200,
      		    body: JSON.stringify(tmp, null, 4)
      	    });
  	    });
  	});
    });
    var tmp = (JSON.parse(Response.body));
    return tmp;
}
