import React, { useState, useEffect, useContext } from 'react';
import { faCashRegister, faChartLine } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Nav, Card, Form, Button, Table, Dropdown, ButtonGroup, InputGroup } from '@themesberg/react-bootstrap';
import { CounterWidget, CircleChartWidget, BarChartWidget, ProductsWidget, ProgressTrackWidget, RankingWidget, SalesValueWidget, SalesValueWidgetPhone, AcquisitionWidget } from "../components/Widgets";
import { PageVisitsTable, TransactionsTable } from "../components/Tables";

import { UserContext } from "./UserContext";
import { AlertTable, AlertControls } from "../components/AlertWidgets"

export default (props) => {
  //User
  const {user, setUser} = useContext(UserContext);
  //Settings
  //Relect the control center
  const [settings, setSettings] = useState({action: "bought", addstroke: "false", animation: "wave", background: "none", client_id: "", code: "OWNER", color: "#fbff00", custom: "null", font: "null", shadow: true, sound: "bits.mp3", stroke: "#AA0B00", volume: 0});
  //Relfect the state of all user settings
  const [defaultSettings, setDefaultSettings] = useState({});
  const [productSettings, setProductSettings] = useState([{}]);
  //List of user alerts
  const [alertList, setAlertList] = useState([]);

  useEffect(() => {
    (async() => {
      var productsTab = document.getElementById("product")

      //Default Settings
      var tmp = await getSettings(user.id);
      setSettings(tmp);
      setDefaultSettings(tmp);

      //Product productSettings
      tmp = await getProductSettings(user.id);
      for (var i = 0; i < tmp.rows.length; i++){
    		var option = document.createElement("option");
    		option.text = tmp.rows[i].short_name
    		option.label = tmp.rows[i].short_name
    		option.value = tmp.rows[i].short_name
        productsTab.add(option)
    	}
      setProductSettings(tmp.rows);

      getAlertNames(user.id, setAlertList)
    })()
  },[])

  return (
    <>
      <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center py-4">
        <h2> Stream Alerts</h2>

        <div className="d-flex ms-auto">
            <Button id="1" variant="secondary" size="sm" className="me-2" onClick={saveSettings} >Save</Button>
            <Button id="2" variant="secondary" size="sm" className="me-2" >Test</Button>
        </div>
      </div>

      <Row className="justify-content-md-center">

        <Col xs={12} sm={6} xl={4} className="mb-4">
          <CounterWidget
            category="Profit"
            title={""}
            period={""}
            percentage={28.4}
            icon={faCashRegister}
            iconColor="shape-tertiary"
          />
        </Col>

        <Col xs={12} sm={6} xl={4} className="mb-4">
          <AlertControls settings={settings} setSettings={setSettings} updateSettings={updateSettings}/>
        </Col>

      </Row>

      <Row>
        <Col >
          <Card border="light" className="bg-white shadow-sm mb-4">
            <Card.Body>
              <h5 className="mb-4">Alert Link</h5>
              <Form>
                <Row>
                  <Col xs={10} className="mb-3">
                    <Form.Group >
                      <Form.Control id="link" value={"https://creatorinventory.com/alerts/"+user.id.toLowerCase()} required type="text" />
                    </Form.Group>

                  </Col>
                  <Col xs={2} className="mb-3">
                    <Button variant="primary" onClick={copyLink}type="button" >Copy</Button>
                  </Col>
                </Row>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <h4> Most recent alerts</h4>
      <Row>
        <Col xs={12} xl={12} className="mb-4">
          <Row>
            <AlertTable data={alertList} />
          </Row>
        </Col>
      </Row>
    </>
  );


  function copyLink(){
    var linkHolder = document.getElementById("link")
    linkHolder.select();
    linkHolder.setSelectionRange(0, 99999); /* For mobile devices */
    document.execCommand("copy");

    alert("Copied the text: " + linkHolder.value);
  }

  async function getSettings(id){
    var url = "https://rf8fbiwmr5.execute-api.us-west-2.amazonaws.com/dev/StreamAlert?id=" + id + "&mode=get";

    const Response = await new Promise((resolve, reject) => {
  	var req = fetch(url, {
  		method: 'GET', // *GET, POST, PUT, DELETE, etc.
  		mode: 'cors', // no-cors, *cors, same-origin
  		cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
  		credentials: 'same-origin', // include, *same-origin, omit
  		headers: {
  		  'Content-Type': 'text/plain',
  		  // 'Content-Type': 'application/x-www-form-urlencoded',
  		},
  		redirect: 'follow', // manual, *follow, error
  		referrerPolicy: 'no-referrer' // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
  	});
  	req.then((result) => {
  	    var res = result.json()
  	    res.then((tmp) => {
  	        resolve({
      		    statusCode: 200,
      		    body: JSON.stringify(tmp, null, 4)
      	    });
  	    });
  	});
    });
    var tmp = (JSON.parse(Response.body));
    return tmp;
  }

  async function getProductSettings(id){
    var url = "https://rf8fbiwmr5.execute-api.us-west-2.amazonaws.com/dev/StreamAlert?id=" + id + "&mode=products";

    const Response = await new Promise((resolve, reject) => {
  	var req = fetch(url, {
  		method: 'GET', // *GET, POST, PUT, DELETE, etc.
  		mode: 'cors', // no-cors, *cors, same-origin
  		cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
  		credentials: 'same-origin', // include, *same-origin, omit
  		headers: {
  		  'Content-Type': 'text/plain',
  		  // 'Content-Type': 'application/x-www-form-urlencoded',
  		},
  		redirect: 'follow', // manual, *follow, error
  		referrerPolicy: 'no-referrer' // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
  	});
  	req.then((result) => {
  	    var res = result.json()
  	    res.then((tmp) => {
  	        resolve({
      		    statusCode: 200,
      		    body: JSON.stringify(tmp, null, 4)
      	    });
  	    });
  	});
    });
    var tmp = (JSON.parse(Response.body));
    return tmp;
  }

  async function getAlertNames(id, setAlertList){
      //Get All Names
      var url = "https://rf8fbiwmr5.execute-api.us-west-2.amazonaws.com/dev/StreamAlert?id=" + id + "&mode=names";
      const Response = await new Promise((resolve, reject) => {
        var req = fetch(url, {
        	method: 'GET', // *GET, POST, PUT, DELETE, etc.
        	mode: 'cors', // no-cors, *cors, same-origin
        	cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
        	credentials: 'same-origin', // include, *same-origin, omit
        	headers: {
        	  'Content-Type': 'text/plain',
        	  // 'Content-Type': 'application/x-www-form-urlencoded',
