import React, { useState, useEffect, useContext } from 'react';
import { faCashRegister, faChartLine } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Card, Form, Button, Dropdown, ButtonGroup, InputGroup } from '@themesberg/react-bootstrap';

import { CounterWidget, CircleChartWidget, BarChartWidget, ProductsWidget, ProgressTrackWidget, RankingWidget, SalesValueWidget, SalesValueWidgetPhone, AcquisitionWidget } from "../components/Widgets";
import { PageVisitsTable, TransactionsTable } from "../components/Tables";

import { UserContext } from "./UserContext";

export default (props) => {

  const emptyPackage = {
    today: {rows:[{sales:0, day:"", profit:0, product_id:""}]},
    sales: {firstDay: "", lastDay: "", total: 0,rows:[{sales:0, day:"", profit:0, product_id:""},{sales:0, day:"", profit:0, product_id:""}]},
    campaigns: [[]],
    products:{}
  }

  //User
