  //UI Widgets
  const [overview, setOverview] = useState(emptyPackage.today);
  const [sales, setSales] = useState(emptyPackage.sales);
  const [campaigns, setCampaigns] = useState(emptyPackage.campaigns);
  const [productsNames, setProductsNames] = useState(emptyPackage.products);

  useEffect(() => {
    (async () => {


    })()
  },[])

  return (
    <>
      <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center py-4">
        <h2> Stream Alerts</h2>

        <div className="d-flex ms-auto">
            <Button id="1" variant="secondary" size="sm" className="me-2" >Save</Button>
            <Button id="2" variant="secondary" size="sm" className="me-2" >Test</Button>
        </div>
      </div>

      <Row className="justify-content-md-center">

        <Col xs={12} sm={6} xl={4} className="mb-4">
          <CounterWidget
            category="Profit"
            title={""}
            period={""}
            percentage={28.4}
            icon={faCashRegister}
            iconColor="shape-tertiary"
          />
        </Col>

        <Col xs={12} sm={6} xl={4} className="mb-4">
          <CircleChartWidget
            title="Products"
            data={[]} />
        </Col>

      </Row>

      <Row>
        <Col >
          <Card border="light" className="bg-white shadow-sm mb-4">
            <Card.Body>
              <h5 className="mb-4">Alert Link</h5>
              <Form>
                <Row>
                  <Col xs={10} className="mb-3">
                    <Form.Group >
                      <Form.Control id="link" value={"CreatorInventory.com/alerts/..."} required type="text" />
                    </Form.Group>

                  </Col>
                  <Col xs={2} className="mb-3">
                    <Button variant="primary" type="button" >Copy</Button>
                  </Col>
                </Row>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Row>
        <Col xs={12} xl={12} className="mb-4">
          <Row>
            <TransactionsTable data={campaigns} />
          </Row>
        </Col>
      </Row>
    </>
  );
};

async function queryDB(start, end, q, id){
    //Building API Call
    var url = "https://d7rc7vsgld.execute-api.us-west-2.amazonaws.com/dev/q?id=" + id;
    if(q != ""){
      url += "&q="+q
    }
    if(start != ""){
      url += "&start="+start
    }
    if(end != ""){
      url += "&end="+end
    }

    //Making Call
    const Response = await new Promise((resolve, reject) => {
  	var req = fetch(url, {
  		method: 'GET', // *GET, POST, PUT, DELETE, etc.
  		mode: 'cors', // no-cors, *cors, same-origin
  		cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
  		credentials: 'same-origin', // include, *same-origin, omit
  		headers: {
  		  'Content-Type': 'text/plain',
  		  // 'Content-Type': 'application/x-www-form-urlencoded',
  		},
  		redirect: 'follow', // manual, *follow, error
  		referrerPolicy: 'no-referrer' // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
  	});
  	req.then((result) => {
  	    var res = result.json()
  	    res.then((tmp) => {
  	        resolve({
      		    statusCode: 200,
      		    body: JSON.stringify(tmp, null, 4)
      	    });
  	    });
  	});
    });
    var tmp = (JSON.parse(Response.body));
    return tmp;
}
