import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCashRegister, faChartLine, faCloudUploadAlt, faPlus, faRocket, faTasks, faUserShield } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Button, Dropdown, ButtonGroup } from '@themesberg/react-bootstrap';

import { CounterWidget, CircleChartWidget, BarChartWidget, ProductsWidget, ProgressTrackWidget, RankingWidget, SalesValueWidget, SalesValueWidgetPhone, AcquisitionWidget } from "../../components/Widgets";
import { PageVisitsTable, TransactionsTable } from "../../components/Tables";

export default (props) => {

  const emptyPackage = {
    today: {rows:[{sales:0, day:"", profit:0, product_id:""}]},
    sales: {rows:[{sales:0, day:"", profit:0, product_id:""},{sales:0, day:"", profit:0, product_id:""}]},
    campaigns: [[]]
  }

   //Client ID
  const [userID, setUserID] = useState(props.history.location.state);
  //UI Widgets
  const [overview, setOverview] = useState(emptyPackage.today);
  const [sales, setSales] = useState(emptyPackage.sales);
  const [campaigns, setCampaigns] = useState(emptyPackage.campaigns);

  useEffect(() => {
    (async () => {
      //Setting log in Package
      var data = await new Promise((resolve, reject) => {
        var req = queryDB("2020-09-10", "2020-09-16", "login", props.history.location.state);
        req.then((result) => {
          resolve({
            statusCode: 200,
            body: JSON.stringify(result, null, 4)
          });
        });
      });
      //Setting up UI
      var logInPackage = JSON.parse(data.body)
      setOverview(prepToday(logInPackage.today));
      setSales(prepSales(logInPackage.sales));
      setCampaigns(prepCampaigns(logInPackage.campaigns));

    })()
  },[userID])

  async function overviewToday(){
    //change color
    var classname = document.getElementById("1").className;
    classname.replace("btn-secondary","btn-primary")
    document.getElementById("1").className = className
    /*
    classname = document.getElementById("2").className;
    classname.replace("btn-primary","btn-secondary")
    document.getElementById("2").className = className

    classname = document.getElementById("3").className;
    classname.replace("btn-primary","btn-secondary")
    document.getElementById("3").className = className
    */
    //Getting today
    var today = new Date();
    var date = formatDate(today);

    var data = await queryDB(date, "", "sales", userID);
    if(data.rows.length == 0){
      setOverview(prepToday({rows:[{sales:0, day:date, profit:0, product_id:""}]}));
    }
    else{
      setOverview(prepToday(data))
    }
  }

  async function overviewWeek(){
    //change color
    /*
    var classname = document.getElementById("1").className;
    classname.replace("btn-primary","btn-secondary")
    document.getElementById("1").className = className

    classname = document.getElementById("2").className;
    classname.replace("btn-secondary","btn-primary")
    document.getElementById("2").className = className

    classname = document.getElementById("3").className;
    classname.replace("btn-primary","btn-secondary")
    document.getElementById("3").className = className
    */

    //Getting today
    var today = new Date();
    var date = formatDate(today);
    today.setDate(today.getDate() - 7)
    var prevDate = formatDate(today);

    var data = await queryDB(date, prevDate, "sales", userID);
    console.log(data)
    if(data.rows.length == 0){
      setOverview(prepToday({rows:[{sales:0, day:date, profit:0, product_id:""}]}));
    }
    else{
      setOverview(prepToday(data))
    }
  }

  async function overviewMonth(){
    //change color
    /*
    var classname = document.getElementById("1").className;
    classname.replace("btn-primary","btn-secondary")
    document.getElementById("1").className = className

    classname = document.getElementById("2").className;
    classname.replace("btn-primary","btn-secondary")
    document.getElementById("2").className = className

    classname = document.getElementById("3").className;
    classname.replace("btn-secondary","btn-primary")
    document.getElementById("3").className = className
    */
    //Getting today
    var today = new Date();
    var firstDay = new Date(today.getFullYear(), today.getMonth(), 1);
    var lastDay = new Date(today.getFullYear(), today.getMonth() + 1, 0);
    var date = formatDate(firstDay);
    var prevDate = formatDate(lastDay);
    console.log(date, prevDate);

    var data = await queryDB(date, prevDate, "sales", userID);
    if(data.rows.length == 0){
      setOverview(prepToday({rows:[{sales:0, day:date, profit:0, product_id:""}]}));
    }
    else{
      setOverview(prepToday(data))
    }
  }

  return (
    <>
      <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center py-4">
        <h2> Sales Overview</h2>

        <div className="d-flex ms-auto">
            <Button id="1" variant="primary" size="sm" className="me-2" onClick={overviewToday}>Today</Button>
            <Button id="2" variant="secondary" size="sm" className="me-2" onClick={overviewWeek}>This Week</Button>
            <Button id="3" variant="secondary" size="sm" className="me-3" onClick={overviewMonth}>This Month</Button>
        </div>
      </div>

      <Row className="justify-content-md-center">
      <Col xs={12} sm={6} xl={4} className="mb-4">
          <CounterWidget
            category="Sales"
            title={overview.sales}
            period={overview.date}
            percentage={0}
            icon={faChartLine}
            iconColor="shape-secondary"
          />
        </Col>

        <Col xs={12} sm={6} xl={4} className="mb-4">
          <CounterWidget
            category="Profit"
            title={parseInt(overview.profit).toFixed(2)}
            period={overview.date}
            percentage={28.4}
            icon={faCashRegister}
            iconColor="shape-tertiary"
          />
        </Col>

        <Col xs={12} sm={6} xl={4} className="mb-4">
          <CircleChartWidget
            title="Products"
            data={overview.products} />
        </Col>

        <Col xs={12} className="mb-4 d-none d-sm-block">
          <SalesValueWidget
            title="Sales Profit"
            value={sales.total}
            data={sales.days}
          />
        </Col>
        <Col xs={12} className="mb-4 d-sm-none">
          <SalesValueWidgetPhone
            title="Sales Value"
            value={sales.total}
            data={sales.days}
          />
        </Col>
      </Row>

      <Row>
        <Col xs={12} xl={12} className="mb-4">
          <Row>
            <TransactionsTable data={campaigns}/>
          </Row>
        </Col>
      </Row>
    </>
  );
};

function prepToday(data){
  var products = {}
  var tmp = {"sales": 0, "profit": 0, "products":[],"date":data.rows[0].day.substring(0,10)};
  for(var i = 0; i < data.rows.length; i++){
    tmp["sales"] = tmp["sales"] + data.rows[i].sales;
    tmp["profit"] = tmp["profit"] + data.rows[i].profit;

    if(products[data.rows[i].product_id]==undefined){
      products[data.rows[i].product_id] = data.rows[i].sales;
    }
    else{
      products[data.rows[i].product_id] = data.rows[i].sales + products[data.rows[i].product_id];
    }
  }

  var i = 0;
  for(const product in products){
    tmp.products.push({"id": i+1, "label": product, "value": products[product]})
    i = i+1;
  }
  return tmp;
}

function prepSales(data){
  var tmp = {"total": 0, "days": {"labels" :[], "series": []}};
  var days = {};
  for(var i = 0; i < data.rows.length; i++){
    if(days[data.rows[i].day.substring(5,10)] == undefined){
      days[data.rows[i].day.substring(5,10)] = data.rows[i].sales;
      tmp.total = tmp.total + data.rows[i].sales
    }
    else{
      days[data.rows[i].day.substring(5,10)] = days[data.rows[i].day.substring(5,10)] + data.rows[i].sales;
    }
  }

  var series = [];
  for(const day in days){
    tmp.days.labels.push(day);
    series.push(days[day]);
  }
  tmp.days.series.push(series);
  return tmp;
}

function prepCampaigns(data){
  var tmp = [];
  for(var i = 0; i < data.rows.length; i++){
    var row = {"products": data.rows[i].img_url, "day": data.rows[i].start_date.substring(0,10), "sales": data.rows[i].sales, "profit": data.rows[i].profit};
    tmp.push(row);
  }

  return tmp;
}

