function formatDate(day){
  if(day.getMonth() < 9){
    var date = day.getFullYear()+'-0'+(day.getMonth()+1)
  }
  else{
    var date = day.getFullYear()+'-'+(day.getMonth()+1)
  }
  if(day.getDate() < 9){
    date = date +'-0'+day.getDate();
  }
  else{
    date = date +'-'+day.getDate();
  }

  return date;
}
