import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleLeft, } from "@fortawesome/free-solid-svg-icons";
import { Col, Row, Container, Form, Card, Button} from '@themesberg/react-bootstrap';
import { Link } from 'react-router-dom';

import { Routes } from "../../routes";
import BgImage from "../../assets/img/illustrations/signin.svg";

export default (props) => {

  return (
    <main>
      <section className="d-flex align-items-center my-5 mt-lg-6 mb-lg-5">
        <Container>
          <p className="text-center">
            <Card.Link as={Link} to={Routes.DashboardOverview.path} className="text-gray-700">
              <FontAwesomeIcon icon={faAngleLeft} className="me-2" /> Back to homepage
            </Card.Link>
          </p>
          <Row className="justify-content-center form-bg-image" style={{ backgroundImage: `url(${BgImage})` }}>
            <Col xs={12} className="d-flex align-items-center justify-content-center">
              <div className="bg-white shadow-soft border rounded border-light p-4 p-lg-5 w-100 fmxw-500">
                <div className="text-center text-md-center mb-4 mt-md-0">
                  <h3 className="mb-0">Sign in using YouTube</h3>
                </div>
                <Form className="mt-4">
                  <Button variant="primary" type="submit" className="w-100" onClick={()=>{
                    //Get the client ID and pass it on
                    var id = ""//"UCMUP9j-QidC_S0KLlO06uCg";
                    console.log("Channel ID: "+ id);
                    if(id == "UCMUP9j-QidC_S0KLlO06uCg"){
                      props.history.push({pathname: Routes.DashboardOverview.path, state: id});
                    }
                  }}>
                    Sign in
                  </Button>
                </Form>

                <div className="mt-3 mb-4 text-center">
                  <span className="fw-normal">Welcome to the Creator Inventory dashboard. Enter by signing in with the Google account associated with your YouTube channel.</span>
                </div>

                <div className="d-flex justify-content-center align-items-center mt-4">
                  <span className="fw-normal">
                    View our
                    <Card.Link as={Link} to={"https://creatorinventory.com/privacy/"} className="fw-bold">
                      {` Privacy Policy `}
                    </Card.Link>
                  </span>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </main>
  );
};
