import React, { useState, useEffect, useContext } from "react";
import moment from "moment-timezone";
import Datetime from "react-datetime";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Card, Form, Button, InputGroup } from '@themesberg/react-bootstrap';


export const GeneralInfoForm = (props) => {
  //Form items
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [birthday, setBirthday] = useState("");
  const [gender, setGender] = useState("");
  const [email, setEmail] = useState("");
  const [paypal_email, setPaypal_Email] = useState("");
  const [ss_api, setSS_Api] = useState("");
  const [shopify_site, setShopify_Site] = useState("");
  //User
  const user = props.user;

  useEffect(() => {
    (() => {
      setFirstname(user.settings.firstname);
      setLastname(user.settings.lastname);
      setBirthday(user.settings.birthday);
      setGender(user.settings.gender);
      setEmail(user.settings.email);
      setPaypal_Email(user.settings.paypal_email);
      setSS_Api(user.settings.ss_api);
      setShopify_Site(user.settings.shopify_site);
    })()
  },[])

  return (
    <Card border="light" className="bg-white shadow-sm mb-4">
      <Card.Body>
        <h5 className="mb-4">General information</h5>
        <Form>
          <Row>
            <Col md={6} className="mb-3">
              <Form.Group>
                <Form.Label>First Name</Form.Label>
                <Form.Control id="firstname" required type="text" placeholder="Enter your first name"/>
              </Form.Group>
            </Col>
            <Col md={6} className="mb-3">
              <Form.Group >
                <Form.Label>Last Name</Form.Label>
                <Form.Control id="lastname" required type="text" placeholder="Also your last name" />
              </Form.Group>
            </Col>
          </Row>
          <Row className="align-items-center">
            <Col md={6} className="mb-3">
              <Form.Group >
                <Form.Label>Birthday</Form.Label>
                <Datetime
                  timeFormat={false}
                  onChange={setBirthday}
                  renderInput={(props, openCalendar) => (
                    <InputGroup >
                      <InputGroup.Text><FontAwesomeIcon icon={faCalendarAlt} /></InputGroup.Text>
                      <Form.Control id="birthday"
                        required
                        type="text"
                        value={birthday ? moment(birthday).format("MM/DD/YYYY") : ""}
                        placeholder="mm/dd/yyyy"
                        onFocus={openCalendar}
                        onChange={() => { }} />
                    </InputGroup>
                  )} />
              </Form.Group>
            </Col>
            <Col md={6} className="mb-3">
              <Form.Group >
                <Form.Label>Gender</Form.Label>
                <Form.Select id="gender" defaultValue="0">
                  <option value="0">Gender</option>
                  <option value="1">Female</option>
                  <option value="2">Male</option>
                  <option value="2">Other</option>
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col md={12} className="mb-3">
              <Form.Group >
                <Form.Label>Email</Form.Label>
                <Form.Control id="email" required type="email" placeholder="name@company.com" />
              </Form.Group>
            </Col>
          </Row>

          <h5 className="my-4">Stream Alert and Payment Information</h5>
          <Row>
            <Col md={12} className="mb-3">
              <Form.Group >
                <Form.Label>Paypal Email</Form.Label>
                <Form.Control id="paypal" required type="text" placeholder="Enter your paypal email" />
              </Form.Group>
            </Col>
            <Col md={12} className="mb-3">
              <Form.Group >
                <Form.Label>Squarespace API Key</Form.Label>
                <Form.Control id="ss" required type="text" placeholder="Enter your Squarespace API Key" />
              </Form.Group>
            </Col>
            <Col md={12} className="mb-3">
              <Form.Group >
                <Form.Label>Shopify site URL</Form.Label>
                <Form.Control id="shopify" required type="text" placeholder="Enter your Shopify site url" />
              </Form.Group>
            </Col>
          </Row>
          <div className="mt-3">
            <Button variant="primary" type="button" onClick={updateDB}>Save All</Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );

  async function updateDB(){
    var updates = {}
    if(document.getElementById("firstname").value != ""){
      updates["firstname"] = document.getElementById("firstname").value;
    }
    if(document.getElementById("lastname").value != ""){
      updates["lastname"] = document.getElementById("lastname").value;
    }
    if(document.getElementById("birthday").value != ""){
      updates["birthday"] = document.getElementById("birthday").value;
    }
    if(document.getElementById("gender").value != 0){
      updates["gender"] = document.getElementById("gender").value;
    }
    if(document.getElementById("email").value != ""){
      updates["email"] = document.getElementById("email").value;
    }
    if(document.getElementById("paypal").value != ""){
      updates["paypal_email"] = document.getElementById("paypal").value;
    }
    if(document.getElementById("ss").value != ""){
      updates["ss_api"] = document.getElementById("ss").value;
    }
    if(document.getElementById("shopify").value != ""){
      updates["shopify_site"] = document.getElementById("shopify").value;
    }

    console.log(updates)
    if(updates != {}){
      //queryDB(JSON.stringify(updates), "", "settings", user.id);
    }

  }

  async function queryDB(start, end, q, id){
      //Building API Call
      var url = "https://d7rc7vsgld.execute-api.us-west-2.amazonaws.com/dev/q?id=" + id;
      if(q != ""){
        url += "&q="+q
      }
      if(start != ""){
        url += "&start="+JSON.stringify(start);
      }
      if(end != ""){
        url += "&end="+end
      }

      //Making Call
      const Response = await new Promise((resolve, reject) => {
    	var req = fetch(url, {
    		method: 'GET', // *GET, POST, PUT, DELETE, etc.
    		mode: 'cors', // no-cors, *cors, same-origin
    		cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
    		credentials: 'same-origin', // include, *same-origin, omit
    		headers: {
    		  'Content-Type': 'text/plain',
    		  // 'Content-Type': 'application/x-www-form-urlencoded',
    		},
    		redirect: 'follow', // manual, *follow, error
    		referrerPolicy: 'no-referrer' // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
    	});
    	req.then((result) => {
    	    var res = result.json()
    	    res.then((tmp) => {
    	        resolve({
        		    statusCode: 200,
        		    body: JSON.stringify(tmp, null, 4)
        	    });
    	    });
    	});
      });
      var tmp = (JSON.parse(Response.body));
      return tmp;
  }
};
