import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleDown, faAngleUp, faChartArea, faChartBar, faChartLine, faFlagUsa, faFolderOpen, faGlobeEurope, faPaperclip, faUserPlus } from '@fortawesome/free-solid-svg-icons';
import { faAngular, faBootstrap, faReact, faVuejs } from "@fortawesome/free-brands-svg-icons";
import { Col, Row, Card, Image, Button, Table, ListGroup, ProgressBar } from '@themesberg/react-bootstrap';
import { CircleChart, BarChart, SalesValueChart, SalesValueChartphone } from "./Charts";

import Profile1 from "../assets/img/team/profile-picture-1.jpg";
import ProfileCover from "../assets/img/profile-cover.jpg";

import teamMembers from "../data/teamMembers";

export const AlertDisplay = (props) => {
  return (
    <Card border="light" className="shadow-sm">
      <Card.Body>
        <div id = "alertDisplay">
          <div id = "alertContainer">
            <img id="alertBg" class = "float" src=""/>
            <div id = "c" class = "float">
              <div id="alert">
                <div id = "top">
                  <h1 class = 'varText' id = "name">Name </h1>
                  <h1 class = "staticText" id = "action"></h1>
                </div>
                <div id = "gif">
                  <img src="" id = "img"/>
                </div>
                <div id = "bottom">
                  <h1 class = 'varText' id = "product">Product Name!</h1>
                  <h1 class = 'staticText' id = "code"> </h1>
                </div>
              </div>
            </div>
          </div>
        </div>
      </Card.Body>
    </Card>
  );
};

export const AlertControls = (props) => {
  return (
    <Card border="light" className="shadow-sm">
      <Card.Body>
        <div id = "alertControl">
          <div id = "controlContainer">
            <div id = "productSetting" class = "controlBar">
              <h3> Product:</h3>
              <select name="productSettings" id="productSettings" class = "selectionBox" onChange="changeSettings(this)">
                <option value="default">Default</option>
              </select>
            </div>
            <div id = "default" class = "controlBar" style = "display:none">
              <h3> Use default settings:</h3>
              <input type="checkbox" id="defaults" name="defaults" onChange="changeDefault(this)" />
            </div>
            <div id = "background" class = "controlBar">
              <h3> Background:</h3>
              <select name="backgrounds" id="backgrounds" class = "selectionBox" onChange="changeBG(this)">
                <option value="none">None</option>
              </select>
            </div>
            <div id = "animation" class = "controlBar">
              <h3> Animation:</h3>
              <select name="aniamtions" id="animations" class = "selectionBox">
                <option value="none">None</option>
              </select>
            </div>
            <div id = "sound" class = "controlBar">
              <h3> Sound:</h3>
              <select name="sounds" id="sounds" class = "selectionBox">
                <option value="blops.mp3">Blops</option>
              </select>
            </div>
            <div id = "font" class = "controlBar">
              <h3> Font:</h3>
              <select name="fonts" id="fonts" class = "selectionBox" onChange="changeFont(this)">
                <option value="null">Default</option>
              </select>
            </div>
            <div id = "color" class = "controlBar">
              <h3> Color:</h3>
              <input type="color" id="colors" name="colors" value="#e66465" onChange="changeColor(this)" />
            </div>
            <div id = "stroke" class = "controlBar">
              <h3> Stroke:</h3>
              <input type="checkbox" id="addstrokes" name="addstrokes" onChange="addStroke(this)" />
              <input type="color" id="strokes" name="strokes" value="#e66465" onChange="changeStroke(this)" />
            </div>
            <div id = "shadow" class = "controlBar">
              <h3> Drop Shadow:</h3>
              <input type="checkbox" id="shadows" name="shadows" onChange="changeShadow(this)" />
            </div>
            <div id = "volume" class = "controlBar">
              <h3> Volume:</h3>
              <input type="range" min="1" max="100" value="50" class="volume" id="volumes" />
            </div>
            <div id = "message" class = "controlBar">
              <h3> Message:</h3>
              <input id="messages" type="text" value="Name "  onChange="changeText(this)"/>
            </div>
          </div>
        </div>
      </Card.Body>
    </Card>
  );
};

export const CounterWidget = (props) => {
  const { icon, iconColor, category, title, period, percentage } = props;
  const percentageIcon = percentage < 0 ? faAngleDown : faAngleUp;
  const percentageColor = percentage < 0 ? "text-danger" : "text-success";

  return (
    <Card border="light" className="shadow-sm">
      <Card.Body>
        <Row className="d-block d-xl-flex align-items-center">
          <Col xl={5} className="text-xl-center d-flex align-items-center justify-content-xl-center mb-3 mb-xl-0">
            <div className={`icon icon-shape icon-md icon-${iconColor} rounded me-4 me-sm-0`}>
              <FontAwesomeIcon icon={icon} />
            </div>
            <div className="d-sm-none">
              <h5>{category}</h5>
              <h3 className="mb-1">{title}</h3>
            </div>
          </Col>
          <Col xs={12} xl={7} className="px-xl-0">
            <div className="d-none d-sm-block">
              <h5>{category}</h5>
              <h3 className="mb-1">{title}</h3>
            </div>
            <small>on {period}, <FontAwesomeIcon icon={faGlobeEurope} size="xs" /> WorldWide</small>
          </Col>
        </Row>
      </Card.Body>
    </Card>
  );
};

export const AlertsTable = (props) => {
  const { data } = props;
  console.log(data)
  const TableRow = (props) => {
    const { date, name, order } = props;

    return (
      <tr>
        <td>
          <span className="fw-normal">
            {date}
          </span>
        </td>
        <td>
          <span className="fw-normal">
            {name}
          </span>
        </td>
        <td>
          <span className="fw-normal">
            {order}
          </span>
        </td>
      </tr>
    );
  };

  return (
    <Card border="light" className="table-wrapper table-responsive shadow-sm">
      <Card.Body className="pt-0">
        <Table hover className="user-table align-items-center">
          <thead>
            <tr>
              <th className="border-bottom">Date</th>
              <th className="border-bottom">Name</th>
              <th className="border-bottom">Order</th>
            </tr>
          </thead>
          <tbody>
            {data.map(t => <TableRow key={`transaction-${t.products}`} {...t} />)}
          </tbody>
        </Table>
      </Card.Body>
    </Card>
  );
};

export const AlertTable = (props) => {
  const { data } = props;

  const TableRow = (props) => {
    const { date, name, order } = props;

    return (
      <tr>
        <td>
          <span className="fw-normal">
            {date}
          </span>
        </td>
        <td>
          <span className="fw-normal">
            {name}
          </span>
        </td>
        <td>
          <span className="fw-normal">
            {order}
          </span>
        </td>
      </tr>
    );
  };

  return (
    <Card border="light" className="table-wrapper table-responsive shadow-sm">
      <Card.Body className="pt-0">
        <Table hover className="user-table align-items-center">
          <thead>
            <tr>
              <th className="border-bottom">Date</th>
              <th className="border-bottom">Name</th>
              <th className="border-bottom">Order</th>
            </tr>
          </thead>
          <tbody>
            {data.map(t => <TableRow key={`transaction-${t.products}`} {...t} />)}
          </tbody>
        </Table>
      </Card.Body>
    </Card>
  );
};
