import React, { useContext, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleLeft, } from "@fortawesome/free-solid-svg-icons";
import { Col, Row, Container, Form, Card, Button} from '@themesberg/react-bootstrap';
import { Link } from 'react-router-dom';
import { UserContext } from "../UserContext";
import useScript from '../../hooks/useScript';

import { Routes } from "../../routes";
import BgImage from "../../assets/img/illustrations/signin.svg";

//gAPI
import { gapi, loadAuth2, loadGapiInsideDOM, loadClientAuth2, loadAuth2WithProps} from 'gapi-script'


export default (props) => {
  const CLIENT_ID = '640109321718-hi3cr72v38sa783htp8ihbd84qh6i56p.apps.googleusercontent.com';
  const DISCOVERY_DOCS = ["https://www.googleapis.com/discovery/v1/apis/youtube/v3/rest"];
  const SCOPES = 'https://www.googleapis.com/auth/youtube.readonly';

  useEffect(() => {
    const setAuth2 = async () => {
      const auth2 = await loadAuth2WithProps(gapi, {clientId:CLIENT_ID, scope:SCOPES, discoveryDocs:DISCOVERY_DOCS})
      if (auth2.isSignedIn.get()) {
        let gapiClient = await loadClientAuth2(gapi, CLIENT_ID, SCOPES,DISCOVERY_DOCS);
        new Promise(resolve => {
          gapi.client.load('youtube','v3')
          .then(() => {
            gapi.client.youtube.channels.list({
              "part": [
                "snippet,contentDetails,statistics"
              ],
              "mine": true
            })
            .then(async function(response) {
                // Handle the results here (response.result has the parsed body).
                var channel_id = response.result.items[0]['id'];
                console.log(channel_id)
                props.history.push({pathname: Routes.DashboardOverview.path, state: channel_id});
            });
          });
        });
        //If already loged in
        /*

        */
      }
      else {
        attachSignin(document.getElementById('signInBtn'), auth2);
      }
    }
    setAuth2();
  }, []);

  const attachSignin = (element, auth2) => {
    auth2.attachClickHandler(element, {},
      async (googleUser) => {
        //Handle sign in
        const tmp = await loadGapiInsideDOM();
        console.log(tmp);
        let gapiClient = await loadClientAuth2(gapi, CLIENT_ID, SCOPES);
        console.log(gapiClient);
        //console.log(gapiClient)
        /*
        tmp.youtube.channels.list({
          "part": [
            "snippet,contentDetails,statistics"
          ],
          "mine": true
        })
        .then(async function(response) {
            // Handle the results here (response.result has the parsed body).
            var channel_id = response.result.items[0]['id'];
            console.log(channel_id)
            if(channel_id == "UCMUP9j-QidC_S0KLlO06uCg"){
              props.history.push({pathname: Routes.DashboardOverview.path, state: channel_id});
            }
        });
        */
      }, (error) => {
      console.log(JSON.stringify(error))
    });
  };

  const signOut = () => {
    const auth2 = gapi.auth2.getAuthInstance();
    auth2.signOut().then(() => {
      console.log('User signed out.');
    });
  }

  return (
    <main>
      <section className="d-flex align-items-center my-5 mt-lg-6 mb-lg-5">
        <Container>
          <p className="text-center">
            <Card.Link as={Link} to={Routes.DashboardOverview.path} className="text-gray-700">
              <FontAwesomeIcon icon={faAngleLeft} className="me-2" /> Back to homepage
            </Card.Link>
          </p>
          <Row className="justify-content-center form-bg-image" style={{ backgroundImage: `url(${BgImage})` }}>
            <Col xs={12} className="d-flex align-items-center justify-content-center">
              <div className="bg-white shadow-soft border rounded border-light p-4 p-lg-5 w-100 fmxw-500">
                <div className="text-center text-md-center mb-4 mt-md-0">
                  <h3 className="mb-0">Sign in using YouTube</h3>
                </div>



                <Form className="mt-4">
                <Button id="signInBtn" variant="primary" type="submit" className="w-100">
                  Google OAuth WIP
                </Button>
                <Button id="signOutBtn" variant="primary" type="submit" onClick={signOut} className="w-100">
                  Sign Out
                </Button>
                  <Button variant="primary" type="submit" className="w-100" onClick={()=>{
                    //Get the client ID and pass it on
                    var id = "UCMUP9j-QidC_S0KLlO06uCg";
                    //var id = "UCzRBjHjz9Ud1wmKx5kCUVpQ";
                    //Check for ID, else fail
                    if(id == "UCzRBjHjz9Ud1wmKx5kCUVpQ" || id == "UCMUP9j-QidC_S0KLlO06uCg"){
                      props.history.push({pathname: Routes.DashboardOverview.path, state: id});
                    }
                  }}>
                    Login with Google
                  </Button>
                </Form>

                <div className="mt-3 mb-4 text-center">
                  <span className="fw-normal">Welcome to the Creator Inventory dashboard. Enter by signing in with the Google account associated with your YouTube channel.</span>
                </div>

                <div className="d-flex justify-content-center align-items-center mt-4">
                  <span className="fw-normal">
                    View our
                    <Card.Link as={Link} to={"https://creatorinventory.com/privacy/"} className="fw-bold">
                      {` Privacy Policy `}
                    </Card.Link>
                  </span>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </main>
  );
};
