import React, { useState, useEffect, useContext } from 'react';
import { faCashRegister, faChartLine } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Nav, Card, Form, Button, Table, Dropdown, ButtonGroup, InputGroup } from '@themesberg/react-bootstrap';
import { CounterWidget, CircleChartWidget, BarChartWidget, ProductsWidget, ProgressTrackWidget, RankingWidget, SalesValueWidget, SalesValueWidgetPhone, AcquisitionWidget } from "../components/Widgets";
import { PageVisitsTable, TransactionsTable } from "../components/Tables";

import { UserContext } from "./UserContext";
import { AlertTable, AlertControls, AlertDisplay } from "../components/AlertWidgets"

export default (props) => {
  //User
  const {user, setUser} = useContext(UserContext);
  //Settings
  //Relect the control center
  const [settings, setSettings] = useState({action: "bought", addstroke: "false", animation: "wave", background: "none", client_id: "", code: "OWNER", color: "#fbff00", custom: "null", font: "Bebas Neue/BebasNeue-Regular.ttf", shadow: true, sound: "bits.mp3", stroke: "#AA0B00", volume: 0});
  //Relfect the state of all user settings
  const [defaultSettings, setDefaultSettings] = useState({});
  const [productSettings, setProductSettings] = useState([{}]);
  //List of user alerts
  const [alertList, setAlertList] = useState([]);

  useEffect(() => {
    (async() => {
      var productsTab = document.getElementById("product")

      //Default Settings
      var tmp = await getSettings(user.id);
      setSettings(tmp);
      setDefaultSettings(tmp);

      //Product productSettings
      tmp = await getProductSettings(user.id);
      for (var i = 0; i < tmp.rows.length; i++){
    		var option = document.createElement("option");
    		option.text = tmp.rows[i].short_name
    		option.label = tmp.rows[i].short_name
    		option.value = tmp.rows[i].short_name
        productsTab.add(option)
    	}
      setProductSettings(tmp.rows);

      getAlertNames(user.id, setAlertList)
    })()
  },[])

  return (
    <>
      <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center py-4">
        <h2> Stream Alerts</h2>

        <div className="d-flex ms-auto">
            <Button variant="secondary" size="sm" className="me-2" onClick={saveSettings} >Save</Button>
            <Button variant="secondary" size="sm" className="me-2" onClick={testAlert} >Test</Button>
        </div>
      </div>

      <Row className="justify-content-md-center">

        <Col xs={12} className="mb-4">
          <AlertDisplay
            settings={settings}
          />
        </Col>

      </Row>

      <Row>

        <Col xs={12} className="mb-4">
          <AlertControls settings={settings} setSettings={setSettings} updateSettings={updateSettings}/>
        </Col>

      </Row>

      <Row>
        <Col >
          <Card border="light" className="bg-white shadow-sm mb-4">
            <Card.Body>
              <h5 className="mb-4">Alert Link</h5>
              <Form>
                <Row>
                  <Col xs={10} className="mb-3">
                    <Form.Group >
                      <Form.Control id="link" value={"https://creatorinventory.com/alerts/"+user.id.toLowerCase()} required type="text" />
