import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCashRegister, faChartLine, faCloudUploadAlt, faPlus, faRocket, faTasks, faUserShield } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Button, Dropdown, ButtonGroup } from '@themesberg/react-bootstrap';

import { CounterWidget, CircleChartWidget, BarChartWidget, ProductsWidget, ProgressTrackWidget, RankingWidget, SalesValueWidget, SalesValueWidgetPhone, AcquisitionWidget } from "../../components/Widgets";
import { PageVisitsTable, TransactionsTable } from "../../components/Tables";
import { trafficShares, totalOrders } from "../../data/charts";

export default (props) => {

  const emptyPackage = {
    today: {rows:[{sales:0, day:"", profit:0, product_id:""}]},
    sales: {rows:[{sales:0, day:"", profit:0, product_id:""},{sales:0, day:"", profit:0, product_id:""}]},
    campaigns: {rows:[{active:"",client_id:"", end_date:""}]}
  }

   //Client ID
  const [userID, setUserID] = useState(props.history.location.state);
  const [logInPackage, setLogInPackage] = useState(emptyPackage);
  //UI Widgets
  const [overview, setOverview] = useState(emptyPackage.today);
  const [sales, setSales] = useState(emptyPackage.sales);
  const [campaigns, setCampaigns] = useState(emptyPackage.campaigns);

  useEffect(() => {
    (async () => {
      //Setting log in Package
      var data = await new Promise((resolve, reject) => {
        var req = onLogin(props);
        req.then((result) => {
          resolve({
            statusCode: 200,
            body: JSON.stringify(result, null, 4)
          });
        });
      });
      setLogInPackage(JSON.parse(data.body));
      setOverview(prepToday(JSON.parse(data.body).today));

    })()
  },[userID])


  return (
    <>
      <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center py-4">
        <h2> Sales Overview</h2>

        <div className="d-flex ms-auto">
            <Button variant="primary" size="sm" className="me-2">Today</Button>
            <Button variant="secondary" size="sm" className="me-2">This Week</Button>
            <Button variant="secondary" size="sm" className="me-3">This Month</Button>
        </div>
      </div>

      <Row className="justify-content-md-center">
      <Col xs={12} sm={6} xl={4} className="mb-4">
          <CounterWidget
            category="Sales"
            title={overview.sales}
            period={overview.date}
            percentage={0}
            icon={faChartLine}
            iconColor="shape-secondary"
          />
        </Col>

        <Col xs={12} sm={6} xl={4} className="mb-4">
          <CounterWidget
            category="Profit"
            title={overview.profit}
            period={overview.date}
            percentage={28.4}
            icon={faCashRegister}
            iconColor="shape-tertiary"
          />
        </Col>

        <Col xs={12} sm={6} xl={4} className="mb-4">
          <CircleChartWidget
            title="Products"
            data={trafficShares} />
        </Col>

        <Col xs={12} className="mb-4 d-none d-sm-block">
          <SalesValueWidget
            title="Sales Profit"
            value="10,567"
            percentage={10.57}
          />
        </Col>
        <Col xs={12} className="mb-4 d-sm-none">
          <SalesValueWidgetPhone
            title="Sales Value"
            value="10,567"
            percentage={10.57}
          />
        </Col>
      </Row>

      <Row>
        <Col xs={12} xl={12} className="mb-4">
          <Row>
            <TransactionsTable />
          </Row>
        </Col>
      </Row>
    </>
  );
};

function prepToday(data){
  var products = [];
  var tmp = {"sales": 0, "profit": 0, "products":{},"date":data.rows[0].day.substring(0,10)};
  for(var i = 0; i < data.rows.length; i++){
    tmp["sales"] = tmp["sales"] + data.rows[i].sales;
    tmp["profit"] = tmp["profit"] + data.rows[i].profit;
    var t = {};
    t[data.rows[i].product_id] = data.rows[i].sales;
    products.push(t);
  }
  tmp["products"] = products;
  return tmp;
}


async function onLogin(props){
    //API Params
    var start = "2020-09-10";
    var end = "2020-09-16";
    var id = props.history.location.state;
    var q = "login"

    //Building API Call
    var url = "https://d7rc7vsgld.execute-api.us-west-2.amazonaws.com/dev/q?id=" + id;
    if(q != ""){
      url += "&q="+q
    }
    if(start != ""){
      url += "&start="+start
    }
    if(end != ""){
      url += "&end="+end
    }

    //Making Call
    const Response = await new Promise((resolve, reject) => {
  	var req = fetch(url, {
  		method: 'GET', // *GET, POST, PUT, DELETE, etc.
  		mode: 'cors', // no-cors, *cors, same-origin
  		cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
  		credentials: 'same-origin', // include, *same-origin, omit
  		headers: {
  		  'Content-Type': 'text/plain',
  		  // 'Content-Type': 'application/x-www-form-urlencoded',
  		},
  		redirect: 'follow', // manual, *follow, error
  		referrerPolicy: 'no-referrer' // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
  	});
  	req.then((result) => {
  	    var res = result.json()
  	    res.then((tmp) => {
  	        resolve({
      		    statusCode: 200,
      		    body: JSON.stringify(tmp, null, 4)
      	    });
  	    });
  	});
    });
    var tmp = (JSON.parse(Response.body));
    return tmp;
}
