  async function testAlert(){
    await runAnimation();
    const Alert = document.getElementById('alert');
    Alert.style.animation = "";
    const Gif = document.getElementById("gif")
    Gif.style.animation = "";
    const BG = document.getElementById("alertBg")
    BG.style.animation = "";
