import React, { useState, useEffect, useContext } from "react";
import moment from "moment-timezone";
import Datetime from "react-datetime";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Card, Form, Button, InputGroup } from '@themesberg/react-bootstrap';


export const GeneralInfoForm = (props) => {
  //Form items
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [birthday, setBirthday] = useState("");
  const [gender, setGender] = useState("");
  const [email, setEmail] = useState("");
  const [paypal_email, setPaypal_Email] = useState("");
  const [ss_api, setSS_Api] = useState("");
  const [shopify_site, setShopify_Site] = useState("");
  //User
  const user = props.user;

  useEffect(() => {
    (() => {
      setFirstname(user.settings.firstname);
      setLastname(user.settings.lastname);
      setBirthday(user.settings.birthday);
      setGender(user.settings.gender);
      setEmail(user.settings.email);
      setPaypal_Email(user.settings.paypal_email);
      setSS_Api(user.settings.ss_api);
      setShopify_Site(user.settings.shopify_site);

      console.log(firstname)
    })()
  },[])

  function updateDB(){

  }

  return (
    <Card border="light" className="bg-white shadow-sm mb-4">
      <Card.Body>
        <h5 className="mb-4">General information</h5>
        <Form>
          <Row>
            <Col md={6} className="mb-3">
              <Form.Group id="firstName">
                <Form.Label>First Name</Form.Label>
                <Form.Control required type="text" placeholder="Enter your first name"/>
              </Form.Group>
            </Col>
            <Col md={6} className="mb-3">
              <Form.Group id="lastName">
                <Form.Label>Last Name</Form.Label>
                <Form.Control required type="text" placeholder="Also your last name" />
              </Form.Group>
            </Col>
          </Row>
          <Row className="align-items-center">
            <Col md={6} className="mb-3">
              <Form.Group id="birthday">
                <Form.Label>Birthday</Form.Label>
                <Datetime
                  timeFormat={false}
                  onChange={setBirthday}
                  renderInput={(props, openCalendar) => (
                    <InputGroup>
                      <InputGroup.Text><FontAwesomeIcon icon={faCalendarAlt} /></InputGroup.Text>
                      <Form.Control
                        required
                        type="text"
                        value={birthday ? moment(birthday).format("MM/DD/YYYY") : ""}
                        placeholder="mm/dd/yyyy"
                        onFocus={openCalendar}
                        onChange={() => { }} />
                    </InputGroup>
                  )} />
              </Form.Group>
            </Col>
            <Col md={6} className="mb-3">
              <Form.Group id="gender">
                <Form.Label>Gender</Form.Label>
                <Form.Select defaultValue="0">
                  <option value="0">Gender</option>
                  <option value="1">Female</option>
                  <option value="2">Male</option>
                  <option value="2">Other</option>
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col md={12} className="mb-3">
              <Form.Group id="emal">
                <Form.Label>Email</Form.Label>
                <Form.Control required type="email" placeholder="name@company.com" />
              </Form.Group>
            </Col>
          </Row>

          <h5 className="my-4">Stream Alert and Payment Information</h5>
          <Row>
            <Col md={12} className="mb-3">
              <Form.Group id="firstName">
                <Form.Label>Paypal Email</Form.Label>
                <Form.Control required type="text" placeholder="Enter your paypal email" />
              </Form.Group>
            </Col>
            <Col md={12} className="mb-3">
              <Form.Group id="lastName">
                <Form.Label>Squarespace API Key</Form.Label>
                <Form.Control required type="text" placeholder="Enter your Squarespace API Key" />
              </Form.Group>
            </Col>
            <Col md={12} className="mb-3">
              <Form.Group id="lastName">
                <Form.Label>Shopify site URL</Form.Label>
                <Form.Control required type="text" placeholder="Enter your Shopify site url" />
              </Form.Group>
            </Col>
          </Row>
          <div className="mt-3">
            <Button variant="primary" type="submit">Save All</Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};
