    		url = url + "&code=" + settings.code;
    		url = url + "&color=" + (settings.color).substring(1);
    		url = url + "&action=" + settings.message.substring(5).trim();
    		url = url + "&stroke=" + (settings.stroke).substring(1);
    		url = url + "&addstroke=" + settings.addStrokes.checked;
    		url = url + "&shadow=" + settings.shadow.checked;
  			url = url + "&pid=" + settings.product_id;
  			url = url + "&usedefault=false"
  			url = url + "&shortname=" + settings.short_name;

        //TODO: UPDATE PRODUCT SETTINGS VAR
        /*
  			var options = document.getElementById("productSettings").options
