  				if(options[i].label == currentProduct.short_name){
  					var Option = options[i];
  					currentProduct.animation = animation.value;
  					currentProduct.background = background.value;
  					currentProduct.sound = sound.value;
  					currentProduct.font = font.value;
  					currentProduct.volume = (volume.value/100).toString();
  					currentProduct.color = color.value;
  					currentProduct.message = message.value.substring(5).trim();
  					currentProduct.stroke = (stroke.value).substring(1);
